INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(1, 1, 'Bulbasaur', 5, 8, 'A strange seed was planted on its back at birth. The plant sprouts and grows with this POKéMON.', 1, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(2, 2, 'Ivysaur', 5, 8, 'When the bulb on its back grows large, it appears to lose the ability to stand on its hind legs.', 2, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(3, 3, 'Venusaur', 5, 8, 'The plant blooms when it is absorbing solar energy. It stays on the move to seek sunlight.', 3, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(4, 4, 'Charmander', 2, NULL, 'Obviously prefers hot places. When it rains, steam is said to spout from the tip of its tail.', 4, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(5, 5, 'Charmeleon', 2, NULL, 'When it swings its burning tail, it elevates the temperature to unbearably high levels.', 5, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(6, 6, 'Charizard', 2, 10, 'Spits fire that is hot enough to melt boulders. Known to cause forest fires unintentionally.', 6, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(7, 7, 'Squirtle', 3, NULL, 'After birth, its back swells and hardens into a shell. Powerfully sprays foam from its mouth.', 7, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(8, 8, 'Wartortle', 3, NULL, 'Often hides in water to stalk unwary prey. For swimming fast, it moves its ears to maintain balance.', 8, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(9, 9, 'Blastoise', 3, NULL, 'A brutal POKéMON with pressurized water jets on its shell. They are used for high speed tackles.', 9, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(10, 10, 'Caterpie', 12, NULL, 'Its short feet are tipped with suction pads that enable it to tirelessly climb slopes and walls.', 10, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(11, 11, 'Metapod', 12, NULL, 'This POKéMON is vulnerable to attack while its shell is soft, exposing its weak and tender body.', 11, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(12, 12, 'Butterfree', 12, 10, 'In battle, it flaps its wings at high speed to release highly toxic dust into the air.', 12, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(13, 13, 'Weedle', 12, 8, 'Often found in forests, eating leaves. It has a sharp venomous stinger on its head.', 13, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(14, 14, 'Kakuna', 12, 8, 'Almost incapable of moving, this POKéMON can only harden its shell to protect itself from predators.', 14, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(15, 15, 'Beedrill', 12, 8, 'Flies at high speed and attacks using its large venomous stingers on its forelegs and tail.', 15, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(16, 16, 'Pidgey', 1, 10, 'A common sight in forests and woods. It flaps its wings at ground level to kick up blinding sand.', 16, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(17, 17, 'Pidgeotto', 1, 10, 'Very protective of its sprawling territorial area, this POKéMON will fiercely peck at any intruder.', 17, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(18, 18, 'Pidgeot', 1, 10, 'When hunting, it skims the surface of water at high speed to pick off unwary prey such as MAGIKARP.', 18, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(19, 19, 'Rattata', 1, NULL, 'Bites anything when it attacks. Small and very quick, it is a common sight in many places.', 19, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(20, 20, 'Raticate', 1, NULL, 'It uses its whiskers to maintain its balance. It apparently slows down if they are cut off.', 20, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(21, 21, 'Spearow', 1, 10, 'Eats bugs in grassy areas. It has to flap its short wings at high speed to stay airborne.', 21, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(22, 22, 'Fearow', 1, 10, 'With its huge and magnificent wings, it can keep aloft without ever having to land for rest.', 22, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(23, 23, 'Ekans', 8, NULL, 'Moves silently and stealthily. Eats the eggs of birds, such as PIDGEY and SPEAROW, whole.', 23, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(24, 24, 'Arbok', 8, NULL, 'It is rumored that the ferocious warning markings on its belly differ from area to area.', 24, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(25, 25, 'Pikachu', 4, NULL, 'When several of these POKéMON gather, their electricity could build and cause lightning storms.', 25, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(26, 26, 'Raichu', 4, NULL, 'Its long tail serves as a ground to protect itself from its own high voltage power.', 26, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(27, 27, 'Sandshrew', 9, NULL, 'Burrows deep underground in arid locations far from water. It only emerges to hunt for food.', 27, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(28, 28, 'Sandslash', 9, NULL, 'Curls up into a spiny ball when threatened. It can roll while curled up to attack or escape.', 28, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(29, 29, 'Nidoran♀', 8, NULL, 'Although small, its venomous barbs render this POKéMON dangerous. The female has smaller horns.', 29, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(30, 30, 'Nidorina', 8, NULL, 'The femaleés horn develops slowly. Prefers physical attacks such as clawing and biting.', 30, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(31, 31, 'Nidoqueen', 8, 9, 'Its hard scales provide strong protection. It uses its hefty bulk to execute powerful moves.', 31, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(32, 32, 'Nidoran♂', 8, NULL, 'Stiffens its ears to sense danger. The larger its horns, the more powerful its secreted venom.', 32, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(33, 33, 'Nidorino', 8, NULL, 'An aggressive POKéMON that is quick to attack. The horn on its head secretes a powerful venom.', 33, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(34, 34, 'Nidoking', 8, 9, 'It uses its powerful tail in battle to smash, constrict, then break the preyés bones.', 34, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(35, 35, 'Clefairy', 18, NULL, 'Its magical and cute appeal has many admirers. It is rare and found only in certain areas.', 35, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(36, 36, 'Clefable', 18, NULL, 'A timid fairy POKéMON that is rarely seen. It will run and hide the moment it senses people.', 36, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(37, 37, 'Vulpix', 2, NULL, 'At the time of birth, it has just one tail. The tail splits from its tip as it grows older.', 37, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(38, 38, 'Ninetales', 2, NULL, 'Very smart and very vengeful. Grabbing one of its many tails could result in a 1000-year curse.', 38, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(39, 39, 'Jigglypuff', 1, 18, 'When its huge eyes light up, it sings a mysteriously soothing melody that lulls its enemies to sleep.', 39, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(40, 40, 'Wigglytuff', 1, 18, 'The body is soft and rubbery. When angered, it will suck in air and inflate itself to an enormous size.', 40, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(41, 41, 'Zubat', 8, 10, 'Forms colonies in perpetually dark places. Uses ultrasonic waves to identify and approach targets.', 41, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(42, 42, 'Golbat', 8, 10, 'Once it strikes, it will not stop draining energy from the victim even if it gets too heavy to fly.', 42, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(43, 43, 'Oddish', 5, 8, 'During the day, it keeps its face buried in the ground. At night, it wanders around sowing its seeds.', 43, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(44, 44, 'Gloom', 5, 8, 'The fluid that oozes from its mouth isnét drool. It is a nectar that is used to attract prey.', 44, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(45, 45, 'Vileplume', 5, 8, 'The larger its petals, the more toxic pollen it contains. Its big head is heavy and hard to hold up.', 45, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(46, 46, 'Paras', 12, 5, 'Burrows to suck tree roots. The mushrooms on its back grow by drawing nutrients from the bug host.', 46, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(47, 47, 'Parasect', 12, 5, 'A host-parasite pair in which the parasite mushroom has taken over the host bug. Prefers damp places.', 47, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(48, 48, 'Venonat', 12, 8, 'Lives in the shadows of tall trees where it eats insects. It is attracted by light at night.', 48, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(49, 49, 'Venomoth', 12, 8, 'The dust-like scales covering its wings are color coded to indicate the kinds of poison it has.', 49, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(50, 50, 'Diglett', 9, NULL, 'Lives about one yard underground where it feeds on plant roots. It sometimes appears above ground.', 50, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(51, 51, 'Dugtrio', 9, NULL, 'A team of DIGLETT triplets. It triggers huge earthquakes by burrowing 60 miles underground.', 51, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(52, 52, 'Meowth', 1, NULL, 'Adores circular objects. Wanders the streets on a nightly basis to look for dropped loose change.', 52, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(53, 53, 'Persian', 1, NULL, 'Although its fur has many admirers, it is tough to raise as a pet because of its fickle meanness.', 53, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(54, 54, 'Psyduck', 3, NULL, 'While lulling its enemies with its vacant look, this wily POKéMON will use psychokinetic powers.', 54, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(55, 55, 'Golduck', 3, NULL, 'Often seen swimming elegantly by lake shores. It is often mistaken for the Japanese monster, Kappa.', 55, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(56, 56, 'Mankey', 7, NULL, 'Extremely quick to anger. It could be docile one moment then thrashing away the next instant.', 56, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(57, 57, 'Primeape', 7, NULL, 'Always furious and tenacious to boot. It will not abandon chasing its quarry until it is caught.', 57, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(58, 58, 'Growlithe', 2, NULL, 'Very protective of its territory. It will bark and bite to repel intruders from its space.', 58, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(59, 59, 'Arcanine', 2, NULL, 'A POKéMON that has been admired since the past for its beauty. It runs agilely as if on wings.', 59, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(60, 60, 'Poliwag', 3, NULL, 'Its newly grown legs prevent it from running. It appears to prefer swimming than trying to stand.', 60, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(61, 61, 'Poliwhirl', 3, NULL, 'Capable of living in or out of water. When out of water, it sweats to keep its body slimy.', 61, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(62, 62, 'Poliwrath', 3, 7, 'An adept swimmer at both the front crawl and breast stroke. Easily overtakes the best human swimmers.', 62, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(63, 63, 'Abra', 11, NULL, 'Using its ability to read minds, it will identify impending danger and TELEPORT to safety.', 63, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(64, 64, 'Kadabra', 11, NULL, 'It emits special alpha waves from its body that induce headaches just by being close by.', 64, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(65, 65, 'Alakazam', 11, NULL, 'Its brain can outperform a supercomputer. Its intelligence quotient is said to be 5,000.', 65, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(66, 66, 'Machop', 7, NULL, 'Loves to build its muscles. It trains in all styles of martial arts to become even stronger.', 66, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(67, 67, 'Machoke', 7, NULL, 'Its muscular body is so powerful, it must wear a power save belt to be able to regulate its motions.', 67, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(68, 68, 'Machamp', 7, NULL, 'Using its heavy muscles, it throws powerful punches that can send the victim clear over the horizon.', 68, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(69, 69, 'Bellsprout', 5, 8, 'A carnivorous POKéMON that traps and eats bugs. It uses its root feet to soak up needed moisture.', 69, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(70, 70, 'Weepinbell', 5, 8, 'It spits out POISONPOWDER to immobilize the enemy and then finishes it with a spray of ACID.', 70, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(71, 71, 'Victreebel', 5, 8, 'Said to live in huge colonies deep in jungles, although no one has ever returned from there.', 71, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(72, 72, 'Tentacool', 3, 8, 'Drifts in shallow seas. Anglers who hook them by accident are often punished by its stinging acid.', 72, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(73, 73, 'Tentacruel', 3, 8, 'The tentacles are normally kept short. On hunts, they are extended to ensnare and immobilize prey.', 73, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(74, 74, 'Geodude', 13, 9, 'Found in fields and mountains. Mistaking them for boulders, people often step or trip on them.', 74, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(75, 75, 'Graveler', 13, 9, 'Rolls down slopes to move. It rolls over any obstacle without slowing or changing its direction.', 75, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(76, 76, 'Golem', 13, 9, 'Its boulder-like body is extremely hard. It can easily withstand dynamite blasts without damage.', 76, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(77, 77, 'Ponyta', 2, NULL, 'Its hooves are 10 times harder than diamonds. It can trample anything completely flat in little time.', 77, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(78, 78, 'Rapidash', 2, NULL, 'Very competitive, this POKéMON will chase anything that moves fast in the hopes of racing it.', 78, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(79, 79, 'Slowpoke', 3, 11, 'Incredibly slow and dopey. It takes 5 seconds for it to feel pain when under attack.', 79, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(80, 80, 'Slowbro', 3, 11, 'The SHELLDER that is latched onto SLOWPOKEés tail is said to feed on the hostés left over scraps.', 80, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(81, 81, 'Magnemite', 4, 17, 'Uses anti-gravity to stay suspended. Appears without warning and uses THUNDER WAVE and similar moves.', 81, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(82, 82, 'Magneton', 4, 17, 'Formed by several MAGNEMITEs linked together. They frequently appear when sunspots flare up.', 82, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(83, 83, 'Farfetch''d', 1, 10, 'The sprig of green onions it holds is its weapon. It is used much like a metal sword.', 83, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(84, 84, 'Doduo', 1, 10, 'A bird that makes up for its poor flying with its fast foot speed. Leaves giant footprints.', 84, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(85, 85, 'Dodrio', 1, 10, 'Uses its three brains to execute complex plans. While two heads sleep, one head stays awake.', 85, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(86, 86, 'Seel', 3, NULL, 'The protruding horn on its head is very hard. It is used for bashing through thick ice.', 86, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(87, 87, 'Dewgong', 3, 6, 'Stores thermal energy in its body. Swims at a steady 8 knots even in intensely cold waters.', 87, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(88, 88, 'Grimer', 8, NULL, 'Appears in filthy areas. Thrives by sucking up polluted sludge that is pumped out of factories.', 88, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(89, 89, 'Muk', 8, NULL, 'Thickly covered with a filthy, vile sludge. It is so toxic, even its footprints contain poison.', 89, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(90, 90, 'Shellder', 3, NULL, 'Its hard shell repels any kind of attack. It is vulnerable only when its shell is open.', 90, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(91, 91, 'Cloyster', 3, 6, 'When attacked, it launches its horns in quick volleys. Its innards have never been seen.', 91, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(92, 92, 'Gastly', 14, 8, 'Almost invisible, this gaseous POKéMON cloaks the target and puts it to sleep without notice.', 92, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(93, 93, 'Haunter', 14, 8, 'Because of its ability to slip through block walls, it is said to be from another dimension.', 93, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(94, 94, 'Gengar', 14, 8, 'Under a full moon, this POKéMON likes to mimic the shadows of people and laugh at their fright.', 94, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(95, 95, 'Onix', 13, 9, 'As it grows, the stone portions of its body harden to become similar to a diamond, but colored black.', 95, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(96, 96, 'Drowzee', 11, NULL, 'Puts enemies to sleep then eats their dreams. Occasionally gets sick from eating bad dreams.', 96, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(97, 97, 'Hypno', 11, NULL, 'When it locks eyes with an enemy, it will use a mix of PSI moves such as HYPNOSIS and CONFUSION.', 97, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(98, 98, 'Krabby', 3, NULL, 'Its pincers are not only powerful weapons, they are used for balance when walking sideways.', 98, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(99, 99, 'Kingler', 3, NULL, 'The large pincer has 10000 hp of crushing power. However, its huge size makes it unwieldy to use.', 99, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(100, 100, 'Voltorb', 4, NULL, 'Usually found in power plants. Easily mistaken for a POKé BALL, they have zapped many people.', 100, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(101, 101, 'Electrode', 4, NULL, 'It stores electric energy under very high pressure. It often explodes with little or no provocation.', 101, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(102, 102, 'Exeggcute', 5, 11, 'Often mistaken for eggs. When disturbed, they quickly gather and attack in swarms.', 102, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(103, 103, 'Exeggutor', 5, 11, 'Legend has it that on rare occasions, one of its heads will drop off and continue on as an EXEGGCUTE.', 103, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(104, 104, 'Cubone', 9, NULL, 'Because it never removes its skull helmet, no one has ever seen this POKéMONés real face.', 104, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(105, 105, 'Marowak', 9, NULL, 'The bone it holds is its key weapon. It throws the bone skillfully like a boomerang to KO targets.', 105, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(106, 106, 'Hitmonlee', 7, NULL, 'When in a hurry, its legs lengthen progressively. It runs smoothly with extra long, loping strides.', 106, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(107, 107, 'Hitmonchan', 7, NULL, 'While apparently doing nothing, it fires punches in lightning fast volleys that are impossible to see.', 107, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(108, 108, 'Lickitung', 1, NULL, 'Its tongue can be extended like a chameleonés. It leaves a tingling sensation when it licks enemies.', 108, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(109, 109, 'Koffing', 8, NULL, 'Because it stores several kinds of toxic gases in its body, it is prone to exploding without warning.', 109, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(110, 110, 'Weezing', 8, NULL, 'Where two kinds of poison gases meet, 2 KOFFINGs can fuse into a WEEZING over many years.', 110, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(111, 111, 'Rhyhorn', 9, 13, 'Its massive bones are 1000 times harder than human bones. It can easily knock a trailer flying.', 111, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(112, 112, 'Rhydon', 9, 13, 'Protected by an armor-like hide, it is capable of living in molten lava of 3,600 degrees.', 112, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(113, 113, 'Chansey', 1, NULL, 'A rare and elusive POKéMON that is said to bring happiness to those who manage to get it.', 113, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(114, 114, 'Tangela', 5, NULL, 'The whole body is swathed with wide vines that are similar to seaweed. Its vines shake as it walks.', 114, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(115, 115, 'Kangaskhan', 1, NULL, 'The infant rarely ventures out of its motherés protective pouch until it is 3 years old.', 115, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(116, 116, 'Horsea', 3, NULL, 'Known to shoot down flying bugs with precision blasts of ink from the surface of the water.', 116, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(117, 117, 'Seadra', 3, NULL, 'Capable of swimming backwards by rapidly flapping its wing-like pectoral fins and stout tail.', 117, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(118, 118, 'Goldeen', 3, NULL, 'Its tail fin billows like an elegant ballroom dress, giving it the nickname of the Water Queen.', 118, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(119, 119, 'Seaking', 3, NULL, 'In the autumn spawning season, they can be seen swimming powerfully up rivers and creeks.', 119, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(120, 120, 'Staryu', 3, NULL, 'An enigmatic POKéMON that can effortlessly regenerate any appendage it loses in battle.', 120, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(121, 121, 'Starmie', 3, 11, 'Its central core glows with the seven colors of the rainbow. Some people value the core as a gem.', 121, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(122, 122, 'Mr. Mime', 11, 18, 'If interrupted while it is miming, it will slap around the offender with its broad hands.', 122, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(123, 123, 'Scyther', 12, 10, 'With ninja-like agility and speed, it can create the illusion that there is more than one.', 123, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(124, 124, 'Jynx', 6, 11, 'It seductively wiggles its hips as it walks. It can cause people to dance in unison with it.', 124, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(125, 125, 'Electabuzz', 4, NULL, 'Normally found near power plants, they can wander away and cause major blackouts in cities.', 125, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(126, 126, 'Magmar', 2, NULL, 'Its body always burns with an orange glow that enables it to hide perfectly among flames.', 126, 1, 0);
