INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(128, 128, 'Tauros', 1, NULL, 'When it targets an enemy, it charges furiously while whipping its body with its long tails.', 128, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(129, 129, 'Magikarp', 3, NULL, 'In the distant past, it was somewhat stronger than the horribly weak descendants that exist today.', 129, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(130, 130, 'Gyarados', 3, 10, 'Rarely seen in the wild. Huge and vicious, it is capable of destroying entire cities in a rage.', 130, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(131, 131, 'Lapras', 3, 6, 'A POKéMON that has been overhunted almost to extinction. It can ferry people across the water.', 131, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(132, 132, 'Ditto', 1, NULL, 'Capable of copying an enemyés genetic code to instantly transform itself into a duplicate of the enemy.', 132, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(133, 133, 'Eevee', 1, NULL, 'Its genetic code is irregular. It may mutate if it is exposed to radiation from element STONEs.', 133, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(134, 134, 'Vaporeon', 3, NULL, 'Lives close to water. Its long tail is ridged with a fin which is often mistaken for a mermaidés.', 134, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(135, 135, 'Jolteon', 4, NULL, 'It accumulates negative ions in the atmosphere to blast out 10000-volt lightning bolts.', 135, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(136, 136, 'Flareon', 2, NULL, 'When storing thermal energy in its body, its temperature could soar to over 1600 degrees.', 136, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(137, 137, 'Porygon', 1, NULL, 'A POKéMON that consists entirely of programming code. Capable of moving freely in cyberspace.', 137, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(138, 138, 'Omanyte', 13, 3, 'Although long extinct, in rare cases, it can be genetically resurrected from fossils.', 138, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(139, 139, 'Omastar', 13, 3, 'A prehistoric POKéMON that died out when its heavy shell made it impossible to catch prey.', 139, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(140, 140, 'Kabuto', 13, 3, 'A POKéMON that was resurrected from a fossil found in what was once the ocean floor eons ago.', 140, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(141, 141, 'Kabutops', 13, 3, 'Its sleek shape is perfect for swimming. It slashes prey with its claws and drains the body fluids.', 141, 1, 0);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(142, 142, 'Aerodactyl', 13, 10, 'A ferocious, prehistoric POKéMON that goes for the enemyés throat with its serrated saw-like fangs.', 142, 1, 0);
