INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(145, 145, 'Zapdos', 4, 10, 'A legendary bird POKéMON that is said to appear from clouds while dropping enormous lightning bolts.', 145, 1, 1);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(146, 146, 'Moltres', 2, 10, 'Known as the legendary bird of fire. Every flap of its wings creates a dazzling flash of flames.', 146, 1, 1);
INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(147, 147, 'Dratini', 15, NULL, 'Long considered a mythical POKéMON until recently when a small colony was found living underwater.', 147, 1, 0);
