INSERT INTO pokedex.pokemon (id, id_pokedex, name, id_pokemon_type1, id_pokemon_type2, description, id_stats, id_generation, legendary) VALUES(151, 151, 'Mew', 11, NULL, 'So rare that it is still said to be a mirage by many experts. Only a few people have seen it worldwide.', 151, 1, 1);